CREATE TABLE CALENDAR_DATE (ID  BIGSERIAL NOT NULL, HISTORICDAY INT4, HISTORICMONTH INT4, HISTORICYEAR INT4, IDCALENDAR INT8, IDHISTORICMONTH INT8, NAMECALENDAR VARCHAR, NAMEHISTORICMONTH VARCHAR, NEGATIVEYEAR BOOLEAN, ID_HISTORICALDATE INT8 NOT NULL, PRIMARY KEY (ID));
CREATE TABLE CHARACTERS_EVENT (EVENT_ID INT8 NOT NULL, CHARACTER_ID INT8);
CREATE TABLE EVENT (ID  BIGSERIAL NOT NULL, CREATEAT TIMESTAMP NOT NULL, DESCRIPTION VARCHAR NOT NULL, TAGS VARCHAR, TITLE VARCHAR NOT NULL, UPDATEAT TIMESTAMP, HISTORICAL_FINAL_PERIOD_ID INT8, HISTORICAL_FIRST_PERIOD_ID INT8, PRIMARY KEY (ID));
CREATE TABLE HISTORICAL_DATE (ID  BIGSERIAL NOT NULL, DATE TIMESTAMP, PRIMARY KEY (ID));
CREATE TABLE LOCALES_EVENT (EVENT_ID INT8 NOT NULL, LOCALE_ID INT8);
